mapper.select:
	select a.id,  a.product_no, a.user_no, a.issues,a.is_ok,a.status,  a.update_time, a.updater, a.create_time, a.creater, b.product_name,c.img_data
  from t_issues a inner join t_product b on a.product_no = b.product_no left join t_product_img c on b.product_img_no = c.img_no and c.type = 1
        1 = 1 

      and a.product_no like '%${productNo}%' 


      and b.product_name  like '%${productName}%' 


       and a.user_no  like '%${userNo}%'  


      and ( a.user_no in (${pUserNo}) or b.user_no = 'a13' or (b.user_no  in (${pUserNo}) and a.user_no = 'cheng.wb') )


      and a.is_ok = #{isOk,jdbcType=INTEGER}


      and a.status = #{status,jdbcType=INTEGER}


      and a.update_time = #{updateTime,jdbcType=TIMESTAMP}


      and a.updater = #{updater,jdbcType=VARCHAR}


      and a.create_time = #{createTime,jdbcType=TIMESTAMP}


      and a.creater = #{creater,jdbcType=VARCHAR}



      ${orderSql}


      order by a.create_time desc
